import React, { Component } from 'react';
import { Button, Input, Modal } from 'antd';

export default class indentList extends Component {
    state = {
        name: '',
        rolelist: [],
    };
    render() {
        const { rolelist, name } = this.state;
        return (
            <div>
                <Button onClick={this.headleAdd.bind(this)}>添加</Button>
                <Modal title="查看" visible={this.state.isLook} onOk={this.headleLook.bind(this)}>
                    <p>用户Id：{this.state.item.id}</p>
                    <p>用户名：{this.state.item.username}</p>
                    <p>密码：{this.state.item.password}</p>
                    <p>身份：{this.state.item.indent}</p>
                </Modal>
                <div className="indent-box">
                    <li>
                        <span>ID</span>
                        <span>角色</span>
                    </li>
                    {rolelist.map(item => {
                        return (
                            <li key={item.id}>
                                <span>{item.id}</span>
                                <span>{item.name}</span>
                                <span onClick={this.headleDel.bind(this, item.id)}>删除</span>
                            </li>
                        );
                    })}
                </div>
            </div>
        );
    }
    async headleDel(id) {
        let res = await this.http('/deleteRelo', { id }, 'delete');
        const { code, msg } = res.data;
        alert(msg);
        if (code === 1) {
            this.initRole();
        }
    }
    headleAdd() {}
    componentDidMount() {
        this.initRole();
    }
    async initRole() {
        let res = await this.http('/getRole', '', 'get');
        const { code, data } = res.data;
        if (code === 1) {
            this.setState({
                rolelist: data,
            });
        }
    }
}
