import React, { Component } from 'react';
import ReactEcharts from 'echarts-for-react';

export default class work extends Component {
    state = {
        option: {
            title: {
                text: '某站点用户访问来源',
                subtext: '纯属虚构',
                x: 'center',
            },
            tooltip: {
                trigger: 'item',
                formatter: '{a} <br/>{b} : {c} ({d}%)',
            },
            legend: {
                orient: 'vertical',
                x: 'left',
                data: ['直接访问', '邮件营销', '联盟广告', '视频广告', '搜索引擎'],
            },
            toolbox: {
                show: true,
                feature: {
                    mark: { show: true },
                    dataView: { show: true, readOnly: false },
                    magicType: {
                        show: true,
                        type: ['pie', 'funnel'],
                        option: {
                            funnel: {
                                x: '25%',
                                width: '50%',
                                funnelAlign: 'left',
                                max: 1548,
                            },
                        },
                    },
                    restore: { show: true },
                    saveAsImage: { show: true },
                },
            },
            calculable: true,
            series: [
                {
                    name: '访问来源',
                    type: 'pie',
                    radius: '55%',
                    center: ['50%', '60%'],
                    data: [
                        { value: 335, name: '直接访问' },
                        { value: 310, name: '邮件营销' },
                        { value: 234, name: '联盟广告' },
                        { value: 135, name: '视频广告' },
                        { value: 1548, name: '搜索引擎' },
                    ],
                },
            ],
        },
    };
    render() {
        const { option } = this.state;
        return (
            <div>
                <ReactEcharts option={option} style={{ width: 500, height: 300 }}></ReactEcharts>
            </div>
        );
    }
}
