import React, { Component } from 'react';
import Echarts from 'echarts-for-react';

export default class echearts extends Component {
    state = {
        option: {
            xAxis: {
                type: 'category',
                data: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],
            },
            yAxis: {
                type: 'value',
            },
            series: [
                {
                    data: [120, 200, 150, 80, 70, 110, 130],
                    type: 'line',
                    symbol: 'triangle',
                    symbolSize: 20,
                    lineStyle: {
                        color: 'green',
                        width: 4,
                        type: 'dashed',
                    },
                    itemStyle: {
                        borderWidth: 3,
                        borderColor: 'yellow',
                        color: 'blue',
                    },
                },
            ],
        },
    };
    render() {
        return (
            <div>
                <Echarts
                    option={this.state.option}
                    style={{ width: '400', height: '400' }}
                ></Echarts>
            </div>
        );
    }
}
