import Echarts from 'echarts-for-react';
export default class echearts extends Component {
    state = {
        option: {
            title: {
                text: '极坐标双数值轴',
            },
            legend: {
                data: ['line'],
            },
            polar: {},
            tooltip: {
                trigger: 'axis',
                axisPointer: {
                    type: 'cross',
                },
            },
            angleAxis: {
                type: 'value',
                startAngle: 0,
            },
            radiusAxis: {},
            series: [
                {
                    coordinateSystem: 'polar',
                    name: 'line',
                    type: 'line',
                    data: data,
                },
            ],
        },
    };
    render() {
        return (
            <div>
                <ReactEcharts
                    options={this.state.option}
                    style={{ width: '400', height: '400' }}
                ></ReactEcharts>
            </div>
        );
    }
}
